var exports = module.exports = {};

exports.convertDayOfWeek = function(day) {
    var dayOfWeek = "";

    switch (day) {
    	case 0:
		    dayOfWeek = 'Sunday'
		    break;
		case 1:
		    dayOfWeek = 'Monday'
		    break;
		case 2:
		    dayOfWeek = 'Tuesday'
		    break;
		case 3:
		    dayOfWeek = 'Wednesday'
		    break;
		case 4:
		    dayOfWeek = 'Thursday'
		    break;
		case 5:
		    dayOfWeek = 'Friday'
		    break;
		case 6:
		    dayOfWeek = 'Saturday'
		    break;
    }

    return dayOfWeek;
};

exports.convertMonth = function(month) {
    var convertedMonth = "";

    switch(month) {
	    case 0:
	        convertedMonth = 'January'
	        break;
	    case 1:
	        convertedMonth = 'February'
	        break;
	    case 2:
	        convertedMonth = 'March'
	        break;
	    case 3:
	        convertedMonth = 'April'
	        break;
	    case 4:
	        convertedMonth = 'May'
	        break;
	    case 5:
	        convertedMonth = 'June'
	        break;
	    case 6:
	        convertedMonth = 'July'
	        break;
	    case 7:
	        convertedMonth = 'August'
	        break;
	    case 8:
	        convertedMonth = 'September'
	        break;
	    case 9:
	        convertedMonth = 'October'
	        break;
	    case 10:
	        convertedMonth = 'November'
	        break;
	    case 11:
	        convertedMonth = 'December'
	        break;
    };

    return convertedMonth;
};

exports.twoDigitMonth = function(month) {
    var theMonth = month+1; 
    if (theMonth < 10) {
    	return "0" + theMonth;
    }

    return theMonth;
};

exports.twoDigitDay = function(day) {
    if (day < 10) {
    	return "0" + day;
    }

    return day;
};

exports.convertHour = function(hour) {
    if (hour<10) {
    	return "0" + hour;
    }

    return hour;
};

exports.convertMinute = function(min) {
    if (min<10) {
    	return "0" + min;
    }

    return min;
};

exports.convertSecond = function(second) {
    if (second<10) {
    	return "0" + second;
    }

    return second;
};

exports.convertTimezone = function(milHour) {
    return (milHour > 11 ? 'PM' : 'AM');
};